let motionStatus = null;
let activeCount = 0;

let sensors = context.get("sensors") || {};
let wasActive = context.get("active") || false;
let deviceId

if (typeof msg.payload != "undefined" && msg.payload !== null) {
    deviceId = msg.payload.deviceId;
}

if (typeof deviceId != "undefined") {

    // If we didn't receive an event, the process was triggered by something else (eg. enabling the automation)
    motionStatus = msg.payload.value

    if (typeof sensors[deviceId] == "undefined") {
        sensors[deviceId] = {}
    }

    let deviceObject = sensors[deviceId]

    deviceObject.state = motionStatus
    deviceObject.displayName = msg.topic

    context.set("sensors", sensors)
}


for (let [deviceId, values] of Object.entries(sensors)) {
    if ( values.state == 'active' ) {
        activeCount++
    }
}

const active = activeCount > 0

newMessage = {
  topic: env.get("Zone Name"),
  payload: active ? "active" : "inactive",
  count: activeCount
}

    context.set("active", active)
    return newMessage
